    class Ink
    {
        Paper paper = new Paper();
        internal void Plus1(int a, int b)
        {
            if (0 <= a && a < 10 && 0 <= b && b < 10)
            {
                paper.paperList[a][b]++;
            }
        }
        internal void Small(int x, int y)
        {
            Plus1(x - 1, y);
            Plus1(x, y - 1);
            Plus1(x, y);
            Plus1(x + 1, y);
            Plus1(x, y + 1);
        }
        internal void Regular(int x, int y)
        {
            Plus1(x - 1, y - 1);
            Plus1(x, y - 1);
            Plus1(x + 1, y - 1);
            Plus1(x - 1, y);
            Plus1(x, y);
            Plus1(x + 1, y);
            Plus1(x - 1, y + 1);
            Plus1(x, y + 1);
            Plus1(x + 1, y + 1);
        }
        internal void Large(int x, int y)
        {
            Plus1(x, y - 2);
            Plus1(x - 1, y - 1);
            Plus1(x, y - 1);
            Plus1(x + 1, y - 1);
            Plus1(x - 2, y);
            Plus1(x - 1, y);
            Plus1(x, y);
            Plus1(x + 1, y);
            Plus1(x + 2, y);
            Plus1(x - 1, y + 1);
            Plus1(x, y + 1);
            Plus1(x + 1, y + 1);
            Plus1(x, y + 2);
        }
        internal int CountZero()
        {
            int i = 0;
            for (int j = 0; j < 10; ++j)
            {
                i += paper.paperList[j].Count(x => x == 0);
            }
            return i;
        }
        internal int HighNumber()
        {
            List<int> list = new List<int>();
            for (int i = 0; i < 10; ++i)
            {
                list.Add(paper.paperList[i].Max());
            }
            return list.Max();
        }
    }
    class Paper
    {
        internal List<int[]> paperList = new List<int[]>();
        public Paper()
        {
            for (int i = 0; i < 10; ++i)
            {
                paperList.Add(new int[10]);
            }
        }
    }
