var inks = input.trim().split('\n');
for (var i in inks) {
	var ink = inks[i].trim().split(',');
	map[ink[1]][ink[0]] += 1;
	switch (ink[2]) {
		case 3:
			if (ink[1] - 2 >= 0) {
				++map[ink[0]][ink[1] - 2];
			}
			if (ink[1] + 2 < map[0].length) {
				++map[ink[0]][ink[1] + 2];
			}
			if (ink[0] - 2 >= 0) {
				++map[ink[0] - 2][ink[1]];
			}
			if (ink[0] + 2 < map.length) {
				++map[ink[0] + 2][ink[1]];
			}
		case 2:
			if (ink[1] - 1 >= 0 && ink[0] - 1 >= 0) {
				++map[ink[1] - 1][ink[0] - 1];
			}
			if (ink[1] - 1 >= 0 && ink[0] + 1 < map.length) {
				++map[ink[1] - 1][ink[0] + 1];
			}
			if (ink[1] + 1 < map[0].length && ink[0] - 1 >= 0) {
				++map[ink[1] + 1][ink[0] - 1];
			}
			if (ink[1] + 1 < map[0].length && ink[0] + 1 < map.length) {
				++map[ink[1] + 1][ink[0] + 1];
			}
		case 1:
			if (ink[1] - 1 >= 0) {
				++map[ink[0]][ink[1] - 1];
			}
			if (ink[1] + 1 < map[0].length) {
				++map[ink[0]][ink[1] + 1];
			}
			if (ink[0] - 1 >= 0) {
				++map[ink[0] - 1][ink[1]];
			}
			if (ink[0] + 1 < map.length) {
				++map[ink[0] + 1][ink[1]];
			}
			break;
	}
}

