import sys

class Board():

    def __init__(self):
        self.B = [0 for _ in range(12 * 12)]

        for i in range(0, 12):
            self.B[i] = -1
        for i in range(12, 12 * 12, 12):
            self.B[i] = -1
        for i in range(11, 11 * 12, 12):
            self.B[i] = -1
        for i in range(12 * 11, 12 * 12):
            self.B[i] = -1

    def print(self):
        cnt = 0
        for b in self.B:
            if b == 0:
                cnt += 1

        print(cnt)
        print(max(self.B))

    def inc_if_non_wall(self, x, y):
        idx = (y + 1) * 12 + (x + 1)

        if self.B[idx] != -1:
            self.B[idx] += 1

    def drop_s(self, x, y):
        self.inc_if_non_wall(x, y)
        self.inc_if_non_wall(x, y - 1)
        self.inc_if_non_wall(x - 1, y)
        self.inc_if_non_wall(x + 1, y)
        self.inc_if_non_wall(x, y + 1)

    def drop_m(self, x, y):
        self.drop_s(x, y)
        self.inc_if_non_wall(x - 1, y - 1)
        self.inc_if_non_wall(x + 1, y - 1)
        self.inc_if_non_wall(x - 1, y + 1)
        self.inc_if_non_wall(x + 1, y + 1)

    def drop_l(self, x, y):
        self.drop_m(x, y)

        idx = (y + 1) * 12 + (x + 1)
        if 12 * 3 < idx:
            self.inc_if_non_wall(x, y - 2)
        if idx < 12 * 9:
            self.inc_if_non_wall(x, y + 2)

        self.inc_if_non_wall(x - 2, y)
        self.inc_if_non_wall(x + 2, y)


