constexpr int INF = 1 << 30;

void solve(){
    int N, K;
    cin >> N >> K;
    vector<int> A(N);
    for(int i=0;i<N;i++){
        cin >> A[i];
    }
    vector<int> cnt(K+1);
    int ans = INF, s=0;
    for(int l=0,r=0;l<N;l++){
        while(r<N && s<K){
            if(A[r]<=K){
                if(cnt[A[r]]==0) s++;
                cnt[A[r]]++;
            }
            r++;
        }
        if(s==K){
            ans = min(ans, r-l);
        }
        if(A[l]<=K){
            cnt[A[l]]--;
            if(cnt[A[l]]==0) s--;
        }
    }    
    cout << (ans<INF ? ans : 0) << endl;
}

int main() {
    solve();
    return 0;
}

#include <cstdio>
#include <vector>
