#include <map>

int main() {
  size_t N, K;
  scanf("%zu %zu", &N, &K);

  std::vector<size_t> a(N);
  for (auto& ai: a) scanf("%zu", &ai);

  std::map<size_t, int> m;
  size_t ir = 0;
  for (; ir < N; ++ir) {
    if (a[ir] <= K) ++m[a[ir]];
    if (m.size() == K) {
      ++ir;
      break;
    }
  }

  if (m.size() < K)
    return puts("0"), 0;

  size_t res = ir;
  for (size_t i = 0; i < N; ++i) {
    if (a[i] <= K) {
      auto it = m.find(a[i]);
      if (--it->second == 0) m.erase(it);
    }
    while (m.size() < K && ir < N) {
      if (a[ir] <= K) ++m[a[ir]];
      ++ir;
    }
    if (m.size() == K) {
      res = std::min(res, ir-(i+1));
    }
  }

  printf("%zu\n", res);
}

