int main() {
	int N, S;
	scanf("%d %d", &N, &S);

	long long int Ss = S*(S + 1) / 2;

	for (int i = 0; i < N; i++)
	{
		scanf("%d", &a[i]);
	}

	int l = 0, r = -1, L = 10000000;
	long long int t = 0;

	while (r < N)
	{
		if (t == Ss)
		{
			L = min(L, (r - l) + 1);
			if (L == S)
			{
				break;
			}
			c[a[l]]--;
			if (c[a[l]] == 0)
			{
				t -= a[l];
			}
			l++;
		}
		else
		{
			r++;
			if (1 <= a[r] && a[r] <= S)
			{
				c[a[r]]++;
				if (c[a[r]] == 1)
				{
					t += a[r];
				}
			}
			else
			{
				l = r + 1;
				t = 0;
				fill(c, c + S + 1, 0);
			}
		}
	}

	printf("%d\n", L == 10000000 ? 0 : L);
}

