void solve()
{
	READ(int, N, K);
	auto A = read<llong>(N);

	map<int, int> count;
	int type_count = 0;

	int minLen = INF;
	int l = 0;
	int r = 0;
	while (true)
	{
		if (l < r && type_count == K)
		{
			minLen = min(minLen, r - l);

			if (A[l] <= K)
			{
				count[A[l]]--;
				if (count[A[l]] == 0) { type_count--; }
			}
			l++;
		}
		else
		{
			if (r == N) { break; }
			if (A[r]<=K)
			{
				count[A[r]]++;
				if (count[A[r]] == 1) { type_count++; }
			}
			r++;
		}
	}
	int ans = minLen != INF ? minLen : 0;

	WRITE(ans);


}

