// returns min(x : where As[i:i+x) contains all the elements of [1..K])
int smallest_window(int K, vector<int> As){
    vector<int> freq(K + 1, 0);
    int t = K;
    int record = As.size() + 10;
    int tail = 0;
    for (int head = 0; head < As.size(); ++head){
        while (tail < As.size() and t > 0){
            int a = As[tail];
            if (1 <= a <= K){
                ++freq[a];
                if (freq[a] == 1) --t;
            }
            ++tail;
        }
        if (t == 0 and tail - head < record) record = tail - head;
        --freq[As[head]];
        if (freq[As[head]] == 0) ++t;
    }
    if (record > As.size()) return 0;
    return record;
}


