const ll INF = 1e12;

int main() {
  ll N, K; cin >> N >> K;
  deque<int> deq;
  ll ans = INF;
  ll sum = 0;
  map<int, int> mp;
  for (int i = 0; i < N; ++i) {
    int a; cin >> a;
    if (a > K) a = 0;
    sum += a;
    ++mp[a];
    deq.push_back(a);
    while (sum >= K * (K + 1) / 2) {
      ans = min(ans, static_cast<ll>(deq.size()));
      int b = deq.front();
      if (sum - b < K * (K + 1) / 2) break;
      deq.pop_front();
      sum -= b;
      --mp[b];
    }
  }
  cout << ( (ans == INF) ? 0 : ans ) << endl;
  return 0;
}

