using namespace std;

template <class T>
struct SegmentTree {
  // 1-indexed
  typedef function<T(T, T)> F;
  int n;
  F f;
  T unit;
  vector<T> dat;
  SegmentTree(){};
  SegmentTree(int newn, F f, T t) : f(f), unit(t) {
    init(newn);
  }
  void init(int newn) {
    n = 1;
    while(n < newn) n <<= 1;
    dat.assign(n << 1, unit);
  }

  // "go up" process
  void update(int k, T newdata) {
    dat[k += n] = newdata;
    while(k >>= 1) {
      dat[k] = f(dat[(k << 1) | 0], dat[(k << 1) | 1]);
    }
  }
  // [a,b)
  T query(int a, int b) {
    T vl = unit, vr = unit;
    for(int l = a + n, r = b + n; l < r; l >>= 1, r >>= 1) {
      if(l & 1) vl = f(vl, dat[l++]);
      if(r & 1) vr = f(dat[--r], vr);
    }
    return f(vl, vr);
  }
};

long long n, k;
vector<long long> a;

long long solve();

int main() {
  cin >> n >> k;
  a.resize(n);
  for(int i = 0; i < n; ++i) cin >> a[i];
  cout << solve() << endl;
  return 0;
}

long long solve() {
  long long ans = inf, l = 0, r = 0;
  auto f = [](long long l, long long r) {
    return min(l, r);
  };
  SegmentTree<long long> rmq(1000000, f, 1000000);
  for(int i = 0; i < 1000000; ++i) rmq.update(i, 0);
  for(l = 0; l < n; ++l) {
    long long x;
    while(r < n && rmq.query(0, k) == 0) {
      x = rmq.query(a[r] - 1, a[r]);
      rmq.update(a[r++] - 1, 1 + x);
    }
    if(rmq.query(0, k) != 0) ans = min(ans, r - l);
    x = rmq.query(a[l] - 1, a[l]);
    rmq.update(a[l] - 1, x - 1);
  }
  return ans == inf ? 0 : ans;
}

