void solve() {
    i64 ans = INF;

    unordered_map<i64,i64> m;
    for(i64 l=0, r=0; l < N; ++l) {
        for(; r < N; ++r) {
            if(SIZE(m) == K) break;
            if(A[r] <= K)
                ++m[A[r]];
        }
        if(SIZE(m) != K) break;

        chmin(ans, r-l);

        if(A[l] <= K) {
            if(--m[A[l]] == 0)
                m.erase(A[l]);
        }
    }

    PRINTLN(ans == INF ? 0 : ans);
}

