int n,k;

bool proc(vector<int>& a,int t){
    vector<int> c(k,0);
    int cnt = 0;
    REP(i,n){
        if(a[i]<=k){
            int temp = a[i]-1;
            if(c[temp]==0){
                cnt++;
            }
            c[temp]++;
        }
        if(i-t>=0){
            int temp = a[i-t]-1;
            if(temp<k){
                if(c[temp]==1){
                    cnt--;
                }
                c[temp] = max(c[temp]-1,0);
            }

        }
        if(cnt==k){
            return true;
        }


    }
    return false;
}

