long long SmallestWindowII(const vector<long long> &v,const long long K){
  long long n = v.size(),l=0,r=0,ans = 1000000000000000007;
  set<long long> s;
  long long counter[K+1] = {0};
  while(l < n){
    if(s.size() == K){
      ans = min(ans,r-l);
      if(1 <= v[l] && v[l] <= K){
        counter[v[l]]--;
        if(counter[v[l]] == 0)s.erase(v[l]);
      }
      l++;
    }
    else{
      if(r == n)return ans;
      if(1 <= v[r] && v[r] <= K){
        counter[v[r]]++;
        if(counter[v[r]] == 1)s.insert(v[r]);
      }
      r++;
    }
  }
  return ans;
}

