#include <bits/stdc++.h>

using namespace std;

int n, k;
vector<int> V;

bool solve(int ans){
	vector<int> freq(k);

	int tot = 0;

	for (int i = 0; i < ans; ++i){
		int a = V[i];
		if (a >= k) continue;
		if (!freq[a]) tot++;
		freq[a]++;
	}

	for (int i = ans; tot < k && i < n; ++i){
		int a = V[i];
		if (a < k){
			if (!freq[a]) tot++;
			freq[a]++;
		}
		a = V[i - ans];
		if (a < k){
			freq[a]--;
			if (!freq[a]) tot--;
		}
	}

	return tot >= k;
}

int main() {
	cin >> n >> k;

	V = vector<int>(n);
	for (int i = 0; i < n; ++i){
		cin >> V[i];
		V[i]--;
	}

	if (!solve(n)){
		cout << 0 << endl;
		return 0;
	}

	int lo = 0;
	int hi = n;

	while (lo < hi - 1){
		int m = (lo + hi) / 2;
		if (solve(m)) hi = m;
		else lo = m;
	}

	cout << hi << endl;

	return 0;
}

