template <class T>
struct SegmentTree{
  using F = function<T(T,T)>;
  F f;
  T inf;
  int n;
  vector<T> node;

  SegmentTree(vector<T> v,T inf,F f):inf(inf),f(f){
    n = 1; while(n < v.size()) n *= 2;
    node.resize(2*n-1,inf);
    for(int i = 0; i < v.size(); i++) node[i+n-1] = v[i];
    for(int i = n-2; i >= 0; i--) node[i] = f(node[2*i+1],node[2*i+2]);
  }

  void update(int k,T val){
    k += n-1;
    node[k] = val;
    while(k > 0){
      k = (k-1) / 2;
      node[k] = f(node[2*k+1],node[2*k+2]);
    }
  }

  void add(int k,T val){
    k += n-1;
    node[k] += val;
    while(k > 0){
      k = (k-1) / 2;
      node[k] = f(node[2*k+1],node[2*k+2]);
    }
  }

  T getval(int a,int b){ return getval(a,b,0,0,n); }
  T getval(int a,int b,int k,int l,int r){
    //区間[a, b)の値を返す
    if(r <= a || b <= l) return inf;
    if(a <= l && r <= b) return node[k];
    T vl = getval(a, b, 2*k+1, l, (l+r)/2);
    T vr = getval(a, b, 2*k+2, (l+r)/2, r);
    return f(vl, vr);
  }
};

signed main(){

  int n,k; cin >> n >> k;
  vector<int> a(n);
  for(int i = 0; i < n; i++){
    cin >> a[i];
    a[i]--;
  }

  int ans = n+1;
  auto f = [](int x,int y){
    return x + y;
  };

  int j = 0;
  SegmentTree<int> seg(vector<int> (n,0),0,f);
  SegmentTree<int> seg2(vector<int> (n,0),0,f);
  for(int i = 0; i < n; i++){
    while(j < n && seg.getval(0,k) < k){
      seg.update(a[j],1);
      seg2.add(a[j],1);
      j++;
    }
    if(seg.getval(0,k) < k) continue;
    ans = min(ans,j - i);
    seg2.add(a[i],-1);
    if(seg2.getval(a[i],a[i]+1) <= 0) seg.update(a[i],0);
  }
  if(ans == n+1) ans = 0;
  cout << ans << endl;

  return 0;
}

