void solve()
{
    int n, k;
    cin >> n >> k;
    vector<int> as(n);
    for (int i = 0; i < n; i++)
    {
        cin >> as[i];
    }
    int ans = n + 1, r = -1, cnt = 0;
    vector<int> bincnt(k + 1);
    for (int l = 0; l <= n - k; l++)
    {
        while (r < l || (cnt < k && r + 1 < n))
        {
            if (1 <= as[r + 1] && as[r + 1] <= k)
            {
                if (!bincnt[as[r + 1]])
                {
                    cnt++;
                }
                bincnt[as[r + 1]]++;
            }
            r++;
        }
        //cout << l << ' ' << r << endl;
        if (cnt == k)
        {
            ans = min(ans, r - l + 1);
        }
        if (1 <= as[l] && as[l] <= k)
        {
            if (--bincnt[as[l]] == 0)
            {
                cnt--;
            }
        }
    }
    if (ans == n + 1)
    {
        ans = 0;
    }
    cout << ans << endl;
}

