bool check(int x) {
    vector<int> cnt(100001, 0);
    set<int> all;
    for ( int i = 0; i < x; i++ ) {
        if ( a[i] <= K ) all.insert(a[i]);
        cnt[a[i]]++;
    }

    if ( all.size() >= K ) return true;
    for ( int i = 0; i < N-x; i++ ) {
        cnt[a[i]]--;
        if ( !cnt[a[i]] ) all.erase(a[i]);
        if ( a[i+x] <= K ) {
            all.insert(a[i+x]);
            cnt[a[i+x]]++;
        }
        if ( all.size() >= K ) return true;
    }

    return false;
}

