template <typename T>
class segment_tree{
    private:
        static int calc_size(int n){
            int m = 1;
            while(m < n){
                m *= 2;
            }
            return m;
        }

        T query(int s, int t, int i, int l, int r) const {
            if(r <= s || t <= l){
                return init;
            }

            if(s <= l && r <= t){
                return node[i];
            }

            int m = l + (r - l) / 2;
            T vl = query(s, t, i * 2 + 1, l, m);
            T vr = query(s, t, i * 2 + 2, m, r);
            return f(vl, vr);
        }

    public:
        int n;
        vector<T> node;
        T init;
        function<T(T, T)> f;

        segment_tree(int n, T init, function<T(T, T)> f)
            : n(calc_size(n)), node(calc_size(n) * 2, init), init(init), f(f){}

        void update(int i, const T& x){
            i += (n - 1);
            node[i] = x;
            while(i > 0){
                i = (i - 1) / 2;
                node[i] = f(node[2*i+1], node[2*i+2]);
            }
        }

        //[s, t)
        T query(int s, int t) const {
            return query(s, t, 0, 0, n);
        }
};

signed main(){
    int n, k;
    cin >> n >> k;
    vector<int> a(n);
    rep(i, n) cin >> a[i];

    //Range Minimum Query
    segment_tree<int> st(k+1, INF, [](int a, int b){return min(a, b);});
    set<int> s;
    int i;
    for(i = 0; i < n; i++){
        if(a[i] > k){
            continue;
        }
        s.insert(a[i]);
        st.update(a[i], i);
        if((int)s.size() >= k){
            break;
        }
    }

    if((int)s.size() < k){
        cout << 0 << endl;
        return 0;
    }

    int ans = INF;
    for(; i < n; i++){
        if(a[i] > k){
            continue;
        }
        st.update(a[i], i);

        int mini = st.query(1, k+1);

        if(i - mini + 1 < ans){
            ans = i - mini + 1;
        }
    }
    cout << ans << endl;

    return 0;
}

