template< class T >
int slide_smallest2(const vector<T>& v, T k) {
    map<T, int> m;
    int l=0, r;
    int ret = v.size() + 1;
    for (r = 0; r < v.size(); ++r) {
        if (v[r] > k) continue;
        m[v[r]] = r;
        while (m.size() == k) {
            ret = min(ret, r - l + 1);
            if (v[l] <= k && m[v[l]] == l) m.erase(v[l]);
            l++;
        }
    }
    if (ret == v.size() + 1) return 0;
    return ret;
}

