#include<bits/stdc++.h>
using namespace std;
using UL = unsigned int;
using ULL = unsigned long long;
using LL = long long;
#define rep(i, n) for(UL i = 0; i < (n); i++)

struct Problem {

    void Solve() {
        UL N, K; scanf("%d%d", &N, &K);
        vector<UL> A(N); rep(i, N) { scanf("%d", &A[i]); A[i]--; }
        vector<UL> C(K);
        UL Z = 0; UL q = 0;
        UL ans = -1;
        rep(p, N) {
            if (A[p] < K) {
                if (!C[A[p]]) Z++;
                C[A[p]]++;
            }
            while (Z == K) {
                if (A[q] >= K) { q++; continue; }
                if (C[A[q]] != 1) { C[A[q]]--; q++; }
                else break;
            }
            if (Z == K) ans = min(ans, p - q + 1);
        }
        if (ans == -1) printf("0\n");
        else printf("%d\n", ans);
    }
};
int main() {
    unique_ptr<Problem> p(new Problem());
    //cout << fixed << setprecision(10);
    p->Solve();
    return 0;
}

