    int left;
    int right = 0;
    static int map[K_MAX+1];
    int cnt = 0;
    int ans = INF;

    for(left = 0; left < num; left++) {
        for(; right < num; right++) {
            if(cnt == ulimit) break;
            int cur = arr[right];
            if(cur > ulimit) continue; // ignore map
            if(!map[cur]) cnt++;
            map[cur]++;
