	//superintervalも条件を満たすときの尺取り（条件を満たす区間の個数）
	long numSup(int from, int to, int[] a, int k){
        long ans = Integer.MAX_VALUE;
        int[] contain = new int[k+1];
        int l = 0;
        int r = 0;
        if(1<=a[0] && a[0] <=k){
        	contain[a[0]] ++;
        }

        while(true){
        	if(isOK(contain)){
        		ans = Math.min(ans,r-l+1);
                if(1<=a[l] && a[l] <=k){
                	contain[a[l]] --;
                }
        		l ++;
        		if(l>r){
        			r++;
            		if(r>to){
            			break;
            		}
                    if(1<=a[r] && a[r] <=k){
                    	contain[a[r]] ++;
                    }
        		}
        	}
        	else{
        		r ++;
        		if(r>to){
        			break;
        		}
                if(1<=a[r] && a[r] <=k){
                	contain[a[r]] ++;
                }
        	}
        }
        if(ans == Integer.MAX_VALUE){
        	return 0;
        }
        return ans;
	}

	boolean isOK(int[] contain){
		for(int i=1;i<contain.length;i++){
			if(contain[i]==0){
				return false;
			}
		}
		return true;
	}

}


class FastScanner {
	private final InputStream in = System.in;
	private final byte[] buffer = new byte[1024];
	private int ptr = 0;
	private int buflen = 0;
	private boolean hasNextByte() {
		if (ptr < buflen) {
			return true;
		} else {
			ptr = 0;
			try {
				buflen = in.read(buffer);
			} catch (IOException e) {
				e.printStackTrace();
			}
			if (buflen <= 0) {
				return false;
			}
		}
		return true;
	}
	private int readByte() {
		if (hasNextByte())
			return buffer[ptr++];
		else
			return -1;
	}
	private static boolean isPrintableChar(int c) {
		return 33 <= c && c <= 126;
	}
	public boolean hasNext() {
		while (hasNextByte() && !isPrintableChar(buffer[ptr]))
			ptr++;
		return hasNextByte();
	}
	public String next() {
		if (!hasNext())
			throw new NoSuchElementException();
		StringBuilder sb = new StringBuilder();
		int b = readByte();
		while (isPrintableChar(b)) {
			sb.appendCodePoint(b);
			b = readByte();
		}
		return sb.toString();
	}
	public long nextLong() {
		if (!hasNext())
			throw new NoSuchElementException();
		long n = 0;
		boolean minus = false;
		int b = readByte();
		if (b == '-') {
			minus = true;
			b = readByte();
		}
		if (b < '0' || '9' < b) {
			throw new NumberFormatException();
		}
		while (true) {
			if ('0' <= b && b <= '9') {
				n *= 10;
				n += b - '0';
			} else if (b == -1 || !isPrintableChar(b)) {
				return minus ? -n : n;
			} else {
				throw new NumberFormatException();
			}
			b = readByte();
		}
	}
	public int nextInt() {
		long nl = nextLong();
		if (nl < Integer.MIN_VALUE || nl > Integer.MAX_VALUE)
			throw new NumberFormatException();
		return (int) nl;
	}
	public int[] nextintArray(int n){
		int[] a = new int[n];
		for(int i=0;i<n;i++){
			a[i] = nextInt();
		}
		return a;
	}
	public long[] nextlongArray(int n){
		long[] a = new long[n];
		for(int i=0;i<n;i++){
			a[i] = nextLong();
		}
		return a;
	}
	public Integer[] nextIntegerArray(int n){
		Integer[] a = new Integer[n];
		for(int i=0;i<n;i++){
			a[i] = nextInt();
		}
		return a;
	}
	public double nextDouble() {
		return Double.parseDouble(next());
	}
}

using System;
using System.Collections.Generic;
using System.Linq;
using System.IO;
using System.Globalization;
using System.Diagnostics;
using Pair = System.Collections.Generic.KeyValuePair<int, int>;

class Program
{
    static void Main()
    {
        Console.SetOut(new StreamWriter(Console.OpenStandardOutput()) { AutoFlush = false });
        new Program().Solve();
        Console.Out.Flush();
    }
    Scanner cin = new Scanner();


    void Solve()
    {
        int N = cin.Nextint;
        long K = cin.Nextint;
        var A = cin.Scanint;
        var ans = int.MaxValue;
        int l = 0;
        int r = 0;

        var B = new int[K + 1];
        var remain = (1 + K) * K / 2;
        while (l < N)
        {
            while (r < N)
            {
                if (A[r] <= K)
                {
                    if (B[A[r]] == 0)
                    {
                        remain -= A[r];
                    }
                    B[A[r]]++;
                }
                if (remain == 0)
                {
                    break;
                }
                r++;
            }
            //WriteLine($"l={l},r={r}");
            if (r == N)
            {
                break;
            }

            while (l <= r)
            {
                if (A[l] <= K)
                {
                    B[A[l]]--;
                    if (B[A[l]] == 0)
                    {
                        ans = Math.Min(ans, r - l + 1);
                        remain += A[l];
                        break;
                    }
                }
                l++;
            }
            l++;
            r++;
        }
        Console.WriteLine(ans == int.MaxValue ? 0 : ans);
    }

}

