            {
                if (A[r] <= K)
                {
                    if (B[A[r]] == 0)
                    {
                        remain -= A[r];
                    }
                    B[A[r]]++;
                }
                if (remain == 0)
                {
                    break;
                }
                r++;
            }
            //WriteLine($"l={l},r={r}");
            if (r == N)
            {
                break;
            }

            while (l <= r)
            {
                if (A[l] <= K)
                {
                    B[A[l]]--;
                    if (B[A[l]] == 0)
                    {
                        ans = Math.Min(ans, r - l + 1);
                        remain += A[l];
                        break;
                    }
                }
                l++;
            }
            l++;
            r++;
        }
        WriteLine(ans == int.MaxValue ? 0 : ans);
    }

}

