class Node<T>
{
    private int Height { get; set; }
    private Node<T> Parent { get; set; }
    private long size;
    public long Size { get { return Find().size; } set { size = value; } }
    public T Item { get; }

    public Node(T item)
    {
        Height = 0;
        Parent = this;
        Item = item;
        Size = 1;
    }

    public Node<T> Find()
    {
        if (Parent == this)
        {
            return this;
        }
        var p = Parent.Find();
        Parent = p;
        return p;
    }

    public static void Unite(Node<T> a, Node<T> b)
    {
        var p = a.Find();
        var q = b.Find();
        if (p.Height < q.Height)
        {
            p.Parent = q;
            q.Height = Math.Max(p.Height + 1, q.Height);
            q.Size += p.Size;
            p.Size = 0;
        }
        else
        {
            q.Parent = p;
            p.Height = Math.Max(q.Height + 1, p.Height);
            p.Size += q.Size;
            q.Size = 0;
        }
    }

    public static bool Same(Node<T> a, Node<T> b)
    {
        return a.Find().Item.Equals(b.Find().Item);
    }
}

class MyClass
{
    public static void Solve()
    {
        var input = Console.ReadLine().Split().Select(int.Parse).ToArray();
        var N = input[0];
        var K = input[1];
        var A = Console.ReadLine().Split().Select(int.Parse).ToArray();
        var l = 0;
        var r = 0;
        var min = int.MaxValue;
        var set = new HashSet<int>();
        var count = new int[K + 1];
        if (A[0] <= K)
        {
            set.Add(A[0]);
            count[A[0]]++;
        }
        while (l < N && r < N)
        {
            var b = set.Count == K;
            if (b)
            {
                min = Math.Min(min, r - l + 1);
            }
            if (b && l < r)
            {
                if (A[l] <= K)
                {
                    count[A[l]]--;
                    if (count[A[l]] == 0)
                    {
                        set.Remove(A[l]);
                    }
                }
                l++;
            }
            else
            {
                r++;
                if (r < N && A[r] <= K)
                {
                    count[A[r]]++;
                    set.Add(A[r]);
                }
            }
        }
        Console.WriteLine(min == int.MaxValue ? 0 : min);
    }

    public static void Main()
    {
        var sw = new System.IO.StreamWriter(Console.OpenStandardOutput()) { AutoFlush = false };
        Console.SetOut(sw);
        Solve();
        Console.Out.Flush();
    }
}

