var nrs = bufio.NewReaderSize(os.Stdin, 1000000)

func readLineToInt() []int {
    buf := make([]byte, 0, 1000000)
    for {
        l, p, e := nrs.ReadLine()
        if e != nil {
            panic(e)
        }
        buf = append(buf, l...)
        if !p {
            break
        }
    }
    line := strings.Split(string(buf), " ")
    var intLine []int
    for _, str := range line {
        n, _ := strconv.Atoi(str)
        intLine = append(intLine, n)
    }
    return intLine
}

func main() {
    nk := readLineToInt()
    n, k := nk[0], nk[1]
    aList := readLineToInt()
    counter := make(map[int]int)
    left, right := 0, 0
    ans := n + 1
    used := 0
    if aList[0] <= k {
        counter[aList[0]]++
        used++
    }
    for left < n {
        for used < k && right < n - 1{
            right++
            a := aList[right]
            if a <= k {
                v, _ := counter[a]
                if v > 0 {
                    counter[a]++
                } else {
                    counter[a]++
                    used++
                }
            }
        }
        if used < k {
            break
        }
        if ans > right - left + 1 {
            ans = right - left + 1
        }
        v, _ := counter[aList[left]]
        if v == 1 {
            used--
            counter[aList[left]]--
        } else {
            counter[aList[left]]--
        }
        left++
    }
    if ans == n + 1 {
        fmt.Println(0)
    } else {
        fmt.Println(ans)
    }
}

