func solve(scanner *bufio.Scanner, writer *bufio.Writer) {
	n := getNextInt(scanner)
	k := getNextInt(scanner)
	aa := make([]int, n)
	for i := 0; i < n; i++ {
		aa[i] = getNextInt(scanner)
	}
	l := -1
	m := map[int]int{}
	var ans cint
	ans = cint(n + 1)
	for r := 0; r < n; r++ {
		if aa[r] <= k {
			m[aa[r]]++
		}
		for r > l && len(m) >= k {
			ans.minAs(cint(r - l))
			l++
			if m[aa[l]] > 0 {
				m[aa[l]]--
				if m[aa[l]] == 0 {
					delete(m, aa[l])
				}
			}
		}
	}
	if ans == cint(n+1) {
		fmt.Fprintln(writer, 0)
		return
	}
	fmt.Fprintln(writer, ans)
}

type cint int64

func (ct *cint) minAs(x cint) *cint {
	*ct = ct.min(x)
	return ct
}
func (ct *cint) maxAs(x cint) *cint {
	*ct = ct.max(x)
	return ct
}
func (ct cint) min(x cint) cint {
	if ct > x {
		return x
	}
	return ct
}
func (ct cint) max(x cint) cint {
	if ct < x {
		return x
	}
	return ct
}

