use std::cmp::min;
use std::io;
use std::io::prelude::*;

fn main() {
    let mut stdin = io::stdin();
    let mut buf = String::new();
    let _ = stdin.read_to_string(&mut buf);
    let mut iter = buf.split_whitespace();

    let n: usize = iter.next().unwrap().parse().unwrap();
    let k = iter.next().unwrap().parse().unwrap();
    let a: Vec<usize> = iter.map(|nstr| nstr.parse().unwrap()).collect();

    let mut i = 0;
    let mut j = 0;
    let mut occ = vec![0; k + 1];
    let mut dis = 0;
    let mut ans = std::usize::MAX;
    while j < n && ans > k {
        if a[j] <= k {
            if occ[a[j]] == 0 {
                dis += 1;
            }
            occ[a[j]] += 1;
        }
        j += 1;
        while a[i] > k || occ[a[i]] > 1 {
            if a[i] <= k {
                occ[a[i]] -= 1;
            }
            i += 1;
        }
        if dis == k {
            ans = min(ans, j - i);
        }
    }
    if ans == std::usize::MAX {
        ans = 0;
    }

    println!("{}", ans);
}

