fn main() {
    let (n, k) = get!(usize, usize);
    let seq = util::gets::<usize>();
    let mut memo = vec![0; k + 1];
    let mut m = 0;

    let mut r = 0;
    let mut ans = n + 1;

    for l in 0..n {
        while m < k && r < n {
            let x = seq[r];
            if x <= k {
                if memo[x] == 0 {
                    m += 1;
                }
                memo[x] += 1;
            }
            r += 1;
        }
        if m == k {
            ans = min(ans, r - l);
        }

        let x = seq[l];
        if x <= k {
            if memo[x] == 1 {
                m -= 1;
            }
            memo[x] -= 1;
        }
    }

    println!("{}", if ans > n { 0 } else { ans });
}

