const BIG_STACK_SIZE: bool = true;
#[allow(dead_code)]
fn main() {
    use std::thread;
    if BIG_STACK_SIZE {
        thread::Builder::new()
            .stack_size(32 * 1024 * 1024)
            .name("solve".into())
            .spawn(solve)
            .unwrap()
            .join()
            .unwrap();
    } else {
        solve();
    }
}
fn solve() {
    input! {
        N: usize, K: usize,
        A: [usize; N],
    }

    let mut freq = vec![1<<30; 100001];
    for i in 1..K+1 {
        freq[i] = 0;
    }
    let mut zeros = K;

    let mut l = 0;
    let mut min_len = 1<<30;

    for r in 0..N {
        let a = A[r];

        if freq[a] == 0 {
            zeros -= 1;
        }
        freq[a] += 1;

        while freq[A[l]] > 1 {
            freq[A[l]] -= 1;
            l += 1;
        }

        // dbg!((l,r));
        if zeros == 0 {
            min_len = min(min_len, r-l+1);
        }
    }

    let res = if min_len == 1<<30 { 0 } else { min_len };
    println!("{}", res);
}
