		if (p == 0) {
			cin >> t;
			cout << b[t] << endl;
		}
		else if (p == 1) {
			cin >> t;
			if (!b[t])k++;
			b[t] = 1;
		}
		else if (p == 2) {
			cin >> t;
			if (b[t])k--;
			b[t] = 0;
		}
		else if (p == 3) {
			cin >> t;
			if (b[t])k--; else k++;
			b[t] = !b[t];
		}
		else if (p == 4) {
			cout << (k == 64) << endl;
		}
		else if (p == 5) {
			cout << (bool)k << endl;
		}
		else if (p == 6) {
			cout << (k ? 0 : 1) << endl;
		}
		else if (p == 7)cout << k << endl;
		else cout << f() << endl;
	}
