#include<iostream>
#include<cinttypes>

auto test(std::uint64_t* const up, std::uint64_t val) {
	auto& x = *up;
	std::cout << ((x >> val) & 1 ? 1 : 0) << "\n";
}

auto set(std::uint64_t* const up, std::uint64_t val) {
	*up |= (1ull << val);
}

auto clear(std::uint64_t* const up, std::uint64_t val) {
	*up &= ~(1ull << val);
}

auto flip(std::uint64_t* const up, std::uint64_t val) {
	*up ^= (1ull << val);
}


auto all( std::uint64_t x)->decltype(x) {
	bool all = true;
	for (int i{}; i < 64; ++i) {
		all = (x >> i) & 1;
		if (!all)
			break;
	}

	return
		(all ? 1 : 0);
}

auto any( std::uint64_t x)->decltype(x) {
	bool any = false;
	for (int i{}; i < 64; ++i) {
		if (any)	break;
		any = (x >> i) & 1;
	}
	return
		(any ? 1 : 0);
}

auto none( std::uint64_t x)->decltype(x) {
		bool none = true;
		for (int i{}; i < 64; ++i) {
			if (!none)break;
			none = 1 & ~(x >> i);
		}

		return none ? 1 : 0;
}

auto count( std::uint64_t x)->decltype(x){
	decltype(x) count{};
	for (int i{}; i < 64; ++i) {
		count += (x >> i) & 1 ? 1 : 0;
	}
	return count;
}


int main() {

	std::uint64_t x{0};

	int q;
	std::cin >> q;

	for (int i{}; i < q; ++i) {
		std::uint64_t code, val{};
		std::cin >> code;
		if (code <= 3)
			std::cin >> val;

		switch (code)
		{
			case 0:
				test(&x, val);
				break;

			case 1://set 1
				set(&x, val);
				break;

			case 2://set 0
				clear(&x, val);
				break;

			case 3://flip
				flip(&x, val);
				break;

			case 4://all
				val = all(x);
				break;

			case 5:
				val = any(x);
				break;

			case 6:
				val = none(x);
				break;

			case 7:
				val = count(x);
				break;

			case 8:
				val = x;
				break;
			default:
				break;
		}

		if (4 <= code )
			std::cout << val << "\n";

//		std::cout <<"  >>  "<< x << "\n";
	}
}

