unsigned long long getint(int x[], int n){
    unsigned long long ret = 0;
    unsigned long long p[n];
    rep(i, n)
        p[i] = 1ll << i;
    rep(i, n)
        ret += x[i] * p[i];
    return ret;
}

int bitcount(int x[], int n){
    int ret = 0;
    rep(i, n)
        ret += x[i];
    return ret;
}

