bool All(string s){
    rep(i,0,s.size())if(s[i]=='0')return 0;
    return 1;
}

bool any(string s){
    rep(i,0,s.size())if(s[i]=='1')return 1;
    return 0;
}

bool none(string s){
    rep(i,0,s.size())if(s[i]=='1')return 0;
    return 1;
}

int Count(string s){
    int ret=0;
    rep(i,0,s.size())if(s[i]=='1')ret++;
    return ret;
}

ll val(string s){
    ll ret=0;
    rep(i,0,s.size()){
        ret+=pow(2,i)*(s[i]-'0');
    }
    return ret;
}

int main (){
    string s(64,'0');
    int q;cin>>q;
    rep(i,0,q){
        int cmd;cin>>cmd;
