            int i;cin>>i;
            cout<<s[i]<<endl;
        }else if(cmd==1){
            int i;cin>>i;
            s[i]='1';
        }else if(cmd==2){
            int i;cin>>i;
            s[i]='0';
        }else if(cmd==3){
            s[i]=(s[i]=='0')?'1':'0';
        }else if(cmd==4){
            cout<<All(s)<<endl;
        }else if(cmd==5){
            cout<<any(s)<<endl;
        }else if(cmd==6){
            cout<<none(s)<<endl;
        }else if(cmd==7){
            cout<<Count(s)<<endl;
        }else {
            cout<<val(s)<<endl;
        }
        cout<<s<<endl;
    }

}



