#include<bits/stdc++.h>
using namespace std;
using UL = unsigned int;
using ULL = unsigned long long;
using LL = long long;
#define rep(i, n) for(UL i = 0; i < (n); i++)

struct Problem {
public:

	ULL BitNum(ULL a) {
		a = (a & 0x5555555555555555) + ((a >> 1) & 0x5555555555555555);
		a = (a & 0x3333333333333333) + ((a >> 2) & 0x3333333333333333);
		a = (a & 0x0f0f0f0f0f0f0f0f) + ((a >> 4) & 0x0f0f0f0f0f0f0f0f);
		a = (a & 0x00ff00ff00ff00ff) + ((a >> 8) & 0x00ff00ff00ff00ff);
		a = (a & 0x0000ffff0000ffff) + ((a >> 16) & 0x0000ffff0000ffff);
		a = (a & 0x00000000ffffffff) + ((a >> 32) & 0x00000000ffffffff);
		return a;
	}

	void Solve() {
		UL Q; scanf("%u", &Q);
		ULL M = 0;
		while (Q--) {
			UL c; scanf("%u", &c);
			switch (c) {
			case 0: {
				UL i; scanf("%u", &i);
				printf("%llu\n", (M >> i) & 1ull);
			} break;
			case 1: {
				UL i; scanf("%u", &i);
				M |= (1ull << i);
			} break;
			case 2: {
				UL i; scanf("%u", &i);
				M &= ~(1ull << i);
			} break;
			case 3: {
				UL i; scanf("%u", &i);
				M ^= (1ull << i);
			} break;
			case 4: {
				printf("%u\n", !~M);
			} break;
			case 5: {
				printf("%u\n", !!M);
			} break;
			case 6: {
				printf("%u\n", !M);
			} break;
			case 7: {
				printf("%llu\n", BitNum(M));
			} break;
			case 8: {
				printf("%llu\n", M);
			} break;
			}
		}
	}
};

int main() {
	unique_ptr<Problem> p(new Problem());
	p->Solve();
	return 0;
}

