int count(int* flags){
  int retval=0;
  for(int i=0;i<64;i++){
    retval+=flags[i];
  }
  return retval;
}

unsigned long decode(int* flags){
  unsigned long retval=0;
  for(int i=0;i<64;i++){
    retval+=((unsigned long)flags[i])*((unsigned long)pow(2,i));
  }
  return retval;
}

