    return 1;
}

bool any(string s){
    rep(i,0,s.size())if(s[i]=='1')return 1;
    return 0;
}

bool none(string s){
    rep(i,0,s.size())if(s[i]=='1')return 0;
    return 1;
}

int Count(string s){
    int ret=0;
    rep(i,0,s.size())if(s[i]=='1')ret++;
    return ret;
}

ll val(string s){
    //reverse(all(s));
    ll ret=0;
    rep(i,0,s.size()-1){
        ll n=1;
        for(ll j=0;j<i;j++)n*=2LL;
        if(s[i]=='1')ret+=n;
    }
    return ret;
}

