void binPrint(int x, int bits){
  for (int i=bits-1; i>=0; i--){
    cout << (x&(1<<i)?1:0);
  }
  cout << endl;
}
int main(void){
  unsigned long long flg=0;
  int q, qtype, i;

  cin >> q;
  for (int _i=0; _i<q; _i++){
    cin >> qtype;
    if (qtype<=3) cin >> i;
    switch(qtype){
    case 0: // TST
      cout << (flg&(1ull<<i)?1:0) << endl;
      break;
    case 1: // SET
      flg |= 1ull<<i;
      break;
    case 2: // CLR
      flg &= ~(1ull<<i);
      break;
    case 3: // FLP
      flg ^= (1ull<<i);
      break;
    case 4: // ALL
      cout << (~flg==0?1:0) << endl;
      break;
    case 5: // ANY
      cout << (flg?1:0) << endl;
      break;
    case 6: // NON
      cout << (flg==0?1:0) << endl;
      break;
    case 7: // CNT
      i=0;
      for (unsigned long long tmp=flg; tmp; tmp>>=1){
        i += tmp&1;
      }
      cout << i << endl;
      break;
    case 8: // VAL
      cout << flg << endl;
    }
  }
  return 0;
}

