using namespace std;
using ll = long long;
using ul = unsigned long long;
using P = pair<int, int>;
using LP = pair<ll, ll>;

int DIGIT = 64;

int count(ul state) {
    int cnt = 0;
    rep(i, DIGIT) {
        if (state & 1ul) cnt++;
        state = state>>1;
    }
    return cnt;
}

int main() {
    cin.tie(0);
    ios::sync_with_stdio(false);

    int q;
    cin>>q;
    ul state = 0;
    rep(i, q) {
        int c, n;
        cin>>c;
        if (c == 0) {
            cin>>n;
            cout<<((state&(1ll<<n)) != 0)<<endl;
        }
        else if (c == 1) {
            cin>>n;
            state |= (1ul<<n);
        }
        else if (c == 2) {
            cin>>n;
            state &= ~(1ul<<n);
        }
        else if (c == 3) {
            cin>>n;
            state ^= (1ul<<n);
        }
        else if (c == 4) {
            cout<<(count(state)==DIGIT)<<endl;
        }
        else if (c == 5) {
            cout<<(count(state)>0)<<endl;
        }
        else if (c == 6) {
            cout<<(count(state)==0)<<endl;
        }
        else if (c == 7) {
            cout<<count(state)<<endl;
        }
        else {
            cout<<state<<endl;
        }
    }
}

