            // test
            case 0:
            if((f>>i)&1){
                p(1);
            }else{
                p(0);
            }
            break;

            // set
            case 1:
            f |= 1LL<<i;
            break;

            // clear
            case 2:
            {
                ll mask = ~(1LL<<i);
                f &= mask;
            }
            break;

            // flip
            case 3:
            {
                ll mask = 1LL<<i;
                f ^= mask;
            }
            break;

            // all
            case 4:
            {
                ull all_one = -1;
                if(f==all_one){
                    p(1);
                }else{
                    p(0);
                }

            }
            break;

            // any
            case 5:
            if(f==0){
                p(0);
            }else{
                p(1);
            }
            break;

            // none
            case 6:
            if(f==0){
                p(1);
            }else{
                p(0);
            }
            break;

            // count
            case 7:
            {
                ll count = 0;
                ull temp = f;
                while(temp){
                    if(temp&1){
                        count++;
                    }
                    temp = temp>>1;
                }
                p(count);
            }
            break;

            // val
            case 8:
            p(f);
            break;
        }
    }

    return 0;
}

