	array[i] = 1;
	count++;
      }
      break;
    case 2: // clear(i)
      i = (buf[3]=='\n') ? buf[2]-'0' : (buf[2]-'0')*10+buf[3]-'0';
      if (array[i]) {
	array[i] = 0;
	count--;
      }
      break;
    case 3: // clip(i)
      i = (buf[3]=='\n') ? buf[2]-'0' : (buf[2]-'0')*10+buf[3]-'0';
      if (array[i]) {
	array[i] = 0;
	count--;
      } else {
	array[i] = 1;
	count++;
      }
