int binary_search(int* a, int n,int key)
{
	int low, middle, high;

	low = 0, high = n - 1;

	while (low <= high)
	{
		middle = (low + high) / 2;

		if (key < a[middle])
			high = middle - 1;
		else if (key > a[middle])
			low = middle + 1;
		else
			return 1;
	}
	return 0;
}

void reverse(int a[], int b, int e)
{
	int i;

	for (i = 0; i <= (e-b-1) / 2; i++)
	{
		swap(&a[b+i], &a[e - 1 - i]);
	}
}

int lower_bound(int a[], int n, int key)
{
	int low, high, middle;

	low = 0, high = n - 1;

	int i;

	if (key > a[n-1])
		return n;

	if (key < a[0])
		return 0;

	while (low <= high)
	{
		middle = (low + high) / 2;

		if (a[middle] == key)
		{
			for (i = middle; i >= 0; i--)
			{
				if (a[i] != key)
					return i + 1;
			}
			return 0;
		}
		else if (key < a[middle])
			high = middle - 1;
		else if (key > a[middle])
			low = middle + 1;
	}

	if (a[middle] < key)
	{
		for (i = middle; i < n; i++)
		{
			if (a[i] >= key)
				return i;
		}
		return n;
	}
	else
	{
		for (i = middle; i >= 0; i--)
		{
			if (a[i] < key)
				return i + 1;
		}
		return 0;
	}
}

int upper_bound(int a[], int n, int key)
{
	int low, high, middle;

	low = 0, high = n - 1;

	int i;

	if (key > a[n - 1])
		return n;

	if (key < a[0])
		return 0;

	while (low <= high)
	{
		middle = (low + high) / 2;

		if (a[middle] == key)
		{
			for (i = middle; i < n; i++)
			{
				if (a[i] != key)
					return i;
			}
			return n;
		}
		else if (key < a[middle])
			high = middle - 1;
		else if (key > a[middle])
			low = middle + 1;
	}

	if (a[middle] < key)
	{
		for (i = middle; i < n; i++)
		{
			if (a[i] >= key)
				return i;
		}
		return n;
	}
	else
	{
		for (i = middle; i >= 0; i--)
		{
			if (a[i] < key)
				return i + 1;
		}
		return 0;
	}
}

void print_binary(unsigned long long int x)
{
	unsigned long long int mask = (unsigned long long int)1 << 63;

	while (mask)
	{
		if (mask & x)
			printf("1");
		else
			printf("0");

		mask >>= 1;

	}
	printf("\n");
}

void test(int i,unsigned long long int *x)
{
	unsigned long long int mask = (unsigned long long int)1 << i;
	if (mask & *x)
		printf("1");
	else
		printf("0");

	printf("\n");
}

void set(int i, unsigned long long int *x)
{
	unsigned long long int mask = (unsigned long long int)1 << i;

	*x |= mask;
}

void clear(int i, unsigned long long int *x)
{
	unsigned long long int mask = (unsigned long long int)1 << i;
	mask = ~mask;

	*x &= mask;
}

void flip(int i, unsigned long long int *x)
{
	unsigned long long int mask = (unsigned long long int)1 << i;

	*x ^= mask;
}

int count(unsigned long long int *x)
{
	unsigned long long int mask = (unsigned long long int)1 << 63;
	int cnt=0;

	while (mask)
	{
		if (mask & *x)
			cnt++;

		mask>>=1;
	}

	return cnt;
}

void all(unsigned long long int *x)
{
	if (count(x) == 64)
		printf("1");
	else
		printf("0");

	printf("\n");
}

void any(unsigned long long int *x)
{
	if (count(x)>0)
		printf("1");
	else
		printf("0");

	printf("\n");
}

void none(unsigned long long int *x)
{
	if (count(x) == 0)
		printf("1");
	else
		printf("0");

	printf("\n");
}

void val(unsigned long long int *x)
{
	printf("%lu\n", *x);
}

