int main(void)
{
    unsigned long a = 0;
    int i, n;

    scanf("%d", &n);

    int query, set;
    for (i = 0; i < n; i++) {
        int j, count;
        scanf("%d", &query);
        if (query < 4)
            scanf("%d", &set);
        switch (query) {
            case 0: // test(i)
                printf("%d\n", (a & (0x01L << set)) == 0 ? 0 : 1);
                break;
            case 1: // set(i);
                a |= 0x01L << set;
                break;
            case 2: // clear(i);
                a &= 0xffffffffffffffffL ^ (0x01L << set);
                break;
            case 3: // flip(i);
                a ^= 0x01L << set;
                break;
            case 4: // all
                printf("%d\n", a == 0xffffffffffffffffL ? 1 : 0);
                break;
            case 5: // any
                printf("%d\n", a >= 1 ? 1 : 0);
                break;
            case 6: // none
                printf("%d\n", a == 0 ? 1 : 0);
                break;
            case 7: // count
                for (j = 0, count = 0; j < 64; j++) {
                    if (a & (0x01L << j))
                        count++;
                }
                printf("%d\n", count);
                break;
            case 8: // val
                printf("%lu\n", a);
                break;
            //case 9: // val by hex
            //    printf("%lx\n", a);
            //    break;
        }
    }

    return 0;
}

import java.util.*;
import java.io.*;
import java.math.BigInteger;

public class Main {
    static StringBuilder sb;

    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        sb = new StringBuilder();

        BitSet bs = new BitSet(64);

        long q = sc.nextLong();

        for (int i = 0; i < q; i++) {
            int type = sc.nextInt();
            switch (type) {
                case 0:
                    if (bs.get(sc.nextInt())) sb.append(1).append("\n");
                    else sb.append(0).append("\n");
                    break;
                case 1:
                    bs.set(sc.nextInt());
                    break;
                case 2:
                    bs.clear(sc.nextInt());
                    break;
                case 3:
                    bs.flip(sc.nextInt());
                    break;
                case 4:
                    if (bs.cardinality() == 64) sb.append(1).append("\n");
                    else sb.append(0).append("\n");
                    break;
                case 5:
                    if (!bs.isEmpty()) sb.append(1).append("\n");
                    else sb.append(0).append("\n");
                    break;
                case 6:
                    if (bs.isEmpty()) sb.append(1).append("\n");
                    else sb.append(0).append("\n");
                    break;
                case 7:
                    sb.append(bs.cardinality()).append("\n");
                    break;
                case 8:
                    sb.append(toBitInteger(bs)).append("\n");
                    break;
                default:
                    return;
            }
        }

        PrintWriter out = new PrintWriter(System.out);
        out.print(sb);
        out.flush();

        sc.close();
    }

    public static BigInteger toBitInteger(BitSet bs) {
        BigInteger rst = BigInteger.ZERO;
        BigInteger weight = BigInteger.ONE;
        BigInteger two = BigInteger.valueOf(2);
        for (int i = 0; i < 64; i++) {
            if (bs.get(i)) rst = rst.add(weight);
            weight = weight.multiply(two);
        }
        return rst;
    }
}

