                a |= 1uL << i;
            }
            else if (line[0] == "2")
            {
                var i = int.Parse(line[1]);
                a &= ~(1uL << i);
            }
            else if (line[0] == "3")
            {
                var i = int.Parse(line[1]);
                a ^= (1uL << i);
            }
            else if (line[0] == "4")
                sb.Append(bitCount(a) == 64 ? "1\n" : "0\n");
            else if (line[0] == "5")
                sb.Append(a > 0 ? "1\n" : "0\n");
            else if (line[0] == "6")
                sb.Append(a == 0 ? "1\n" : "0\n");
            else if (line[0] == "7")
                sb.Append(bitCount(a).ToString() + "\n");
            else sb.Append(a.ToString() + "\n");
