	con.Println("")
}

func Test(b uint64, n uint) int {
	return int(b >> n & 1)
}
func Set(b *uint64, n uint) {
	(*b) = (*b) | 1<<n
}
func Clear(b *uint64, n uint) {
	(*b) = (*b) & (^(1 << n))
}
func Flip(b *uint64, n uint) {
	(*b) = (*b) ^ 1<<n
}
func All(b *uint64) int {
	if 64 == bits.OnesCount64(*b) {
		return 1
	} else {
		return 0
	}
}
func Any(b *uint64) int {
	if 0 < bits.OnesCount64(*b) {
		return 1
	} else {
		return 0
	}
}
func None(b *uint64) int {
	if 0 == bits.OnesCount64(*b) {
		return 1
	} else {
		return 0
	}
}

func Count(b *uint64) int {
	return bits.OnesCount64(*b)
}

func main() {
	con := NewConsole()
	defer con.Flush()

	var b uint64 = 0
	var q int
	fmt.Scanf("%d", &q)
	lines, _ := ReadLongLines(q)
	for i := range lines {
		args := strings.Split(lines[i], " ")
		command, _ := strconv.Atoi(args[0])
		switch command {
		case 0:
			n, _ := strconv.Atoi(args[1])
			con.Printf("%d\n", Test(b, uint(n)))
		case 1:
			n, _ := strconv.Atoi(args[1])
			Set(&b, uint(n))
		case 2:
			n, _ := strconv.Atoi(args[1])
			Clear(&b, uint(n))
		case 3:
			n, _ := strconv.Atoi(args[1])
			Flip(&b, uint(n))
		case 4:
			con.Printf("%d\n", All(&b))
		case 5:
			con.Printf("%d\n", Any(&b))
		case 6:
			con.Printf("%d\n", None(&b))
		case 7:
			con.Printf("%d\n", Count(&b))
		case 8:
			con.Printf("%d\n", b)
		}
	}

}

