func test(flags uint64, i uint64) bool {
	return (flags & (1 << i)) != 0
}
func set(flags uint64, i uint64) uint64 {
	return flags | (1 << i)
}
func clear(flags uint64, i uint64) uint64 {
	return flags & ^(1 << i)
}
func flip(flags uint64, i uint64) uint64 {
	return flags ^ (1 << i)
}
func all(flags uint64) bool {
	return flags == math.MaxUint64
}
func any(flags uint64) bool {
	return flags != 0
}
func none(flags uint64) bool {
	return flags == 0
}
func count(flags uint64) uint64 {
	cnt := uint64(0)
	for i := 0; i < 64; i++ {
		if (flags & (1 << uint64(i))) != 0 {
			cnt++
		}
	}
	return cnt
}

func main() {
	sc := bufio.NewScanner(os.Stdin)
	sc.Split(bufio.ScanWords)
	q := nextInt(sc)
	wtr := bufio.NewWriter(os.Stdout)
	var flags uint64
	for i := 0; i < q; i++ {
		op := nextInt(sc)
		if op == 0 || op == 1 || op == 2 || op == 3 {
			x := uint64(nextInt(sc))
			if op == 0 {
				if test(flags, x) {
					fmt.Fprintln(wtr, 1)
				} else {
					fmt.Fprintln(wtr, 0)
				}
			} else if op == 1 {
				flags = set(flags, x)
			} else if op == 2 {
				flags = clear(flags, x)
			} else {
				flags = flip(flags, x)
			}
		} else if op == 4 {
			if all(flags) {
				fmt.Fprintln(wtr, 1)
			} else {
				fmt.Fprintln(wtr, 0)
			}
		} else if op == 5 {
			if any(flags) {
				fmt.Fprintln(wtr, 1)
			} else {
				fmt.Fprintln(wtr, 0)
			}
		} else if op == 6 {
			if none(flags) {
				fmt.Fprintln(wtr, 1)
			} else {
				fmt.Fprintln(wtr, 0)
			}
		} else if op == 7 {
			fmt.Fprintln(wtr, count(flags))
		} else {
			fmt.Fprintln(wtr, flags)
		}
	}
	wtr.Flush()
}

