var scanner = bufio.NewScanner(os.Stdin)

func nextString() string {
	scanner.Scan()
	return scanner.Text()
}

func nextInt() uint64 {
	n, err := strconv.Atoi(nextString())
	if err != nil {
		fmt.Printf("strconv.Atoi failed: %v\n", err)
	}
	return uint64(n)
}

func printOneOrZero(ok bool) {
	if ok {
		fmt.Println(1)
	} else {
		fmt.Println(0)
	}
}

func main() {
	scanner.Split(bufio.ScanWords)

	nQueries := int(nextInt())
	n := uint64(0)
	for i := 0; i < nQueries; i++ {
		queryType := nextInt()
		// fmt.Printf("%v %b\n", queryType, n)
		if queryType == 0 { // test
			qi := nextInt()
			mask := uint64(1) << qi
			// fmt.Printf("qi=%v, n & mask = %v", qi, n&mask)
			printOneOrZero(n&mask != 0)
		} else if queryType == 1 { // set
			qi := nextInt()
			n |= (uint64(1) << qi)
		} else if queryType == 2 { // clear
			qi := nextInt()
			// https://stackoverflow.com/questions/23192262/how-would-you-set-and-clear-a-single-bit-in-go
			n &= ^(uint64(1) << qi)
		} else if queryType == 3 { // flip
			qi := nextInt()
			n ^= uint64(1) << qi
		} else if queryType == 4 { // all
			printOneOrZero(bits.OnesCount64(n) == 64)
		} else if queryType == 5 { // any
			printOneOrZero(n != 0)
		} else if queryType == 6 { // none
			printOneOrZero(n == 0)
		} else if queryType == 7 { // count
			fmt.Println(bits.OnesCount64(n))
		} else if queryType == 8 { // val
			fmt.Println(n)
		}
	}
}

