func solve(scanner *bufio.Scanner, writer *bufio.Writer) {
	q := getNextInt(scanner)
	b := uint64(0)
	for i := 0; i < q; i++ {
		t := getNextInt(scanner)
		switch t {
		case 0:
			x := getNextInt(scanner)
			fmt.Fprintln(writer, b>>uint(x)&1)
		case 1:
			x := getNextInt(scanner)
			b = b | (1 << uint(x))
		case 2:
			x := getNextInt(scanner)
			b = b & (math.MaxUint64 ^ (1 << uint(x)))
		case 3:
			x := getNextInt(scanner)
			b = b ^ (1 << uint(x))
		case 4:
			if b == math.MaxUint64 {
				fmt.Fprintln(writer, 1)
				continue
			}
			fmt.Fprintln(writer, 0)
		case 5:
			if b == 0 {
				fmt.Fprintln(writer, 0)
				continue
			}
			fmt.Fprintln(writer, 1)
		case 6:
			if b == 0 {
				fmt.Fprintln(writer, 1)
				continue
			}
			fmt.Fprintln(writer, 0)
		case 7:
			fmt.Fprintln(writer, bits.OnesCount64(b))
		case 8:
			fmt.Fprintln(writer, b)
		}
	}
}

