fn operate<T: IntoIterator<Item = (u8, u8)>>(iter: T) {
  let mut bits = 0u64;

  for (query, i) in iter {
    match query {
      0 => println!("{}",
        if bits & (1u64 << i) != 0 {
          "1"
        } else {
          "0"
        }
      ),

      1 => { bits |= 1u64 << i; },

      2 => { bits &= !(1u64 << i); },

      3 => { bits ^= 1u64 << i; },

      4 => println!("{}",
        if bits == !0u64 { "1" } else { "0" }
      ),

      5 => println!("{}",
        if bits != 0u64 { "1" } else { "0" }
      ),

      6 => println!("{}",
        if bits == 0u64 { "1" } else { "0" }
      ),

      7 => println!("{}", bits.count_ones()),

      8 => println!("{}", bits),

      _ => unreachable!()
    }
  }
}

fn main() {
  let stdin = stdin();
  let mut lines = stdin.lock().lines().map(|l| l.unwrap());

  let n = lines.next().unwrap().parse().unwrap();
  let list = lines.take(n).map(|line| {
    let mut args = line.split_whitespace().map(|s| s.parse::<u8>().unwrap());

    (args.next().unwrap(), args.next().unwrap_or(0))
  });

  operate(list);
}

use std::io;
use std::io::prelude::*;

fn main() {
    let stdin = io::stdin();
    let lock = stdin.lock();
    let mut lines = lock.lines();

    let mut state = 0u64;

    let q = lines.next().unwrap().unwrap().parse().unwrap();

    for (_, l) in (0..q).zip(lines) {
        let l = l.unwrap();
        let mut sw = l.split_whitespace();
        let com = sw.next().unwrap().parse().unwrap();
        let i = sw.next();
        match com {
            0 => {
                let i: u8 = i.unwrap().parse().unwrap();
                println!("{}", if state & (1 << i) != 0 { 1 } else { 0 });
            }
            1 => {
                let i: u8 = i.unwrap().parse().unwrap();
                state |= 1 << i;
            }
            2 => {
                let i: u8 = i.unwrap().parse().unwrap();
                state &= !(1 << i);
            }
            3 => {
                let i: u8 = i.unwrap().parse().unwrap();
                state ^= 1 << i;
            }
            4 => {
                println!("{}", if state == 0xffffffffffffffff { 1 } else { 0 });
            }
            5 => {
                println!("{}", if state != 0 { 1 } else { 0 });
            }
            6 => {
                println!("{}", if state == 0 { 1 } else { 0 });
            }
            7 => {
                println!("{}", state.count_ones());
            }
            _ => {
                println!("{}", state);
            }
        }
    }
}
