#include <algorithm>
#include <iostream>
#include <string>
const int N=14;
int m[N][N]={0};
int f(int y,int x){
	if(m[y][x]==1){
		m[y][x]=0;
		f(y,x-1);
		f(y,x+1);
		f(y-1,x);
		f(y+1,x);
	}
	return 1;
}
int solve(){
	int c=0,i;
	for(i=0;i<N;i++){
		int *p,*q=m[i];
		while(true){
			p=std::find(q,q+N,1);
			if(p==q+N)break;
			c+=f(i,p-q);
		}
	}
	std::cout<<c<<"\n";
	return 0;
}
int main(){
	int y=0;
	std::string s;
	while(getline(std::cin,s)){
		if(y<12){
			y++;
			for(int i=1;i<N-1;i++)m[y][i]=s[i-1]-'0';
		}else y=solve();
	}
	solve();
}

